import React, { useEffect, useState, useRef } from "react";
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";
import InputLabel from "@mui/material/InputLabel";
import FormControl from "@mui/material/FormControl";
import Select from "@mui/material/Select";
import MenuItem from "@mui/material/MenuItem";
import Axios from "axios";
import { useHistory } from "react-router-dom";
import Dialog from "@mui/material/Dialog";
import DialogActions from "@mui/material/DialogActions";
import DialogContent from "@mui/material/DialogContent";
import DialogContentText from "@mui/material/DialogContentText";
import DialogTitle from "@mui/material/DialogTitle";
import Button from "@mui/material/Button";
import { Link } from "react-router-dom";
import Stack from "@mui/material/Stack";

import "../styles/AddAnnonce.css";

export default function AddAnnonce() {
  function addproddash() {
  
    if (
      categorie !== "" &&
      NomproduitRef.current.value !== "" && 
      CodeproduitRef.current.value !== "" &&  
      QuantiteproduitRef.current.value !== "" && 
      PrixproduitRef.current.value !== "" 
    ) 
    {
      Axios.post("http://localhost:3001/addproduitdash", {
        categorie: categorie,
        NomproduitRef: NomproduitRef.current.value,
        CodeproduitRef: CodeproduitRef.current.value,
        QuantiteproduitRef: QuantiteproduitRef.current.value,
        PrixproduitRef: PrixproduitRef.current.value,
      }).then((response) => {
        if (response.data.message === "Operation completed") {
          /** redirect to  list */
          console.log("Operation Completed");
          NomproduitRef.current.value =""
          CodeproduitRef.current.value =""
          QuantiteproduitRef.current.value=""
          PrixproduitRef.current.value=""
          
          history.push({
            //pathname: "/login",
          });
        } else {
          
        }
      });
    }
  }
    const [open, setOpen] = React.useState(false);
    const handleClickOpen = () => {
        setOpen(true);
      };
    
      const handleClose = () => {
        setOpen(false);
      };
  const [categories, setcategories] = useState([""]);
  const [categorie, setcategorie] = useState();
  const [produit, setproduit] = useState();
  const [produits, setproduits] = useState([""]);
  const handleChangecategorie = (event) => {
    setcategorie(event.target.value);
    Axios.post("http://localhost:3001/produitsbycategorie", {
      id: event.target.value,
    }).then((response) => {
      setproduits(response.data);
    });
  };

  useEffect(() => {
    Axios.get("http://localhost:3001/categories").then((response) => {
      setcategories(response.data);
    });
  }, []);

  let history = useHistory();
  const CategorieRef = useRef();
  const NomproduitRef = useRef();
  const CodeproduitRef = useRef();
  const QuantiteproduitRef = useRef();
  const PrixproduitRef = useRef();
  return (
    <div className="global">
        <section className="add-annonce">


            <div className="title">Anouncement</div>
            <div className="content">Put your anouncement here.</div>
            <div className="container">
                <div className="container-body">
                    <div className="right-side">
                        <div className="inputs-container"  >
                            <TextField
                                inputRef={firstnameRef}
                                required
                                error={verfFirstname}
                                onChange={verifFirstname}
                                label="First Name"
                                size="small"
                                sx={{ width: '100%' }}
                            />
                            <TextField
                                inputRef={lastnameRef}
                                required
                                error={verfLastname}
                                onChange={verifLastname}
                                label="Last Name"
                                size="small"
                                sx={{ width: '100%' }}
                            />
                            <TextField
                                inputRef={emailRef}
                                required
                                error={verfEmail}
                                onBlur={verifEmail}
                                label="Email"
                                size="small"
                                sx={{ width: '100%' }}
                            />
                            <TextField
                                inputRef={phoneRef}
                                required
                                error={verfPhone}
                                onChange={verifPhone}
                                label="Phone"
                                size="small"
                                sx={{ width: '100%' }}
                            />
                            <TextField
                                inputRef={cinRef}
                                required
                                error={verfCin}
                                onChange={() => setverfCin(false)}
                                label="Cin"
                                size="small"
                                sx={{ width: '100%' }}
                            />
                            <FormControl fullWidth>
                                <InputLabel size="small" id="demo-simple-select-label">Property</InputLabel>
                                <Select
                                    labelId="demo-simple-select-label"
                                    id="demo-simple-select"
                                    value={property}
                                    label="Property"
                                    size="small"
                                    error={verfProperty}
                                    onChange={handleChangeProperty}
                                >
                                    <MenuItem value={"Room"}>Room</MenuItem>
                                    <MenuItem value={"Appartment"}>Appartment</MenuItem>
                                </Select>
                            </FormControl>
                            <TextField
                                inputRef={dateRef}
                                id="date"
                                error={verfDate}
                                onChange={() => setverfDate(false)}
                                label="Date"
                                type="date"
                                size="small"
                                sx={{ width: "100%" }}
                                InputLabelProps={{
                                    shrink: true,
                                }}
                            />
                            <TextField
                                inputRef={locationRef}
                                required
                                error={verfLocation}
                                onChange={verifLocation}
                                label="Location"
                                size="small"
                                sx={{ width: '100%' }}
                            />
                            <div className="price" >
                                <TextField
                                    inputRef={priceRef}
                                    required
                                    error={verfPrice}
                                    onChange={verifPrice}
                                    label="Price"
                                    size="small"
                                    sx={{ width: '100%' }}
                                />
                            </div>
                            <div className="desc">
                                <TextField
                                    inputRef={descRef}
                                    required
                                    error={verfDesc}    
                                                            
                                    label="Description"
                                    placeholder="Description"
                                    multiline
                                    rows={2}
                                    maxRows={9}
                                />
                            </div>
                        </div>
                        <div className="btns-container">
                            <Button onClick={() => { validate() }} disableElevation variant="contained" className="green_btn">Validate</Button>
                            <Button disableElevation variant="outlined" onClick={handleReset}>Reset</Button>
                            <Dialog
                                open={open}
                                onClose={handleClose}
                                aria-labelledby="alert-dialog-title"
                                aria-describedby="alert-dialog-description"
                            >
                                <DialogTitle id="alert-dialog-title">
                                    {"Please confirm !"}
                                </DialogTitle>
                                <DialogContent>
                                    <DialogContentText id="alert-dialog-description">
                                        Are you sure you want to continue ?
                                    </DialogContentText>
                                </DialogContent>
                                <DialogActions>
                                    <Button onClick={handleClose}>Cancel</Button>
                                    <Link to="/home">
                                        <Button onClick={handleClose} autoFocus>
                                            Confirm
                                        </Button>
                                    </Link>
                                </DialogActions>
                            </Dialog>
                        </div>
                    </div>
                </div>
            </div>
        </section>
    </div>
);
}
